// src/pages/Services.js
import React from 'react';

const Services = ({ setBookings }) => {
  // Sample booking data - this should come from your service data
  const sampleService = {
    id: 1,
    name: "Guitar Lessons",
    price: 50,
    contact: "123-456-7890", // Add contact info for the artisan
  };

  // Handle booking service function
  const handleBookService = () => {
    // Here you can create a booking object
    const booking = {
      serviceId: sampleService.id,
      serviceName: sampleService.name,
      price: sampleService.price,
      contact: sampleService.contact,
    };

    // Update the bookings state in App.js
    setBookings((prevBookings) => [...prevBookings, booking]);
    
    // You can add additional logic like notifications or redirects
    alert(`${sampleService.name} has been booked!`);
  };

  return (
    <div>
      <h1>Services</h1>
      <div>
        <h2>{sampleService.name}</h2>
        <p>Price: ${sampleService.price}</p>
        <p>Contact: {sampleService.contact}</p>
        <button onClick={handleBookService}>Book Now</button>
      </div>
    </div>
  );
};

export default Services;
