import React from 'react';
import './Dashboard.css'; // Import common dashboard CSS

const AdminDashboard = () => {
  // Sample data for users, events, and advertisements
  const users = [
    { id: 1, name: "User 1" },
    { id: 2, name: "User 2" },
    // Add more users as needed
  ];

  const events = [
    { id: 1, title: "Event 1" },
    { id: 2, title: "Event 2" },
    // Add more events as needed
  ];

  const ads = [
    { id: 1, title: "Ad 1" },
    { id: 2, title: "Ad 2" },
    // Add more ads as needed
  ];

  const handleApproveUser = (id) => {
    // Add logic to approve the user (API call)
    alert(`User ${id} approved!`);
  };

  const handleRejectUser = (id) => {
    // Add logic to reject the user (API call)
    alert(`User ${id} rejected!`);
  };

  const handleDeleteUser = (id) => {
    // Add logic to delete the user (API call)
    alert(`User ${id} deleted!`);
  };

  const handleApproveEvent = (id) => {
    // Add logic to approve the event (API call)
    alert(`Event ${id} approved!`);
  };

  const handleRejectEvent = (id) => {
    // Add logic to reject the event (API call)
    alert(`Event ${id} rejected!`);
  };

  const handleApproveAd = (id) => {
    // Add logic to approve the advertisement (API call)
    alert(`Ad ${id} approved!`);
  };

  const handleRejectAd = (id) => {
    // Add logic to reject the advertisement (API call)
    alert(`Ad ${id} rejected!`);
  };

  return (
    <div className="dashboard-container">
      <h1>Admin Dashboard</h1>
      <div className="stats">
        <div className="stat-card">Total Users: {users.length}</div>
        <div className="stat-card">Total Events: {events.length}</div>
        <div className="stat-card">Total Advertisements: {ads.length}</div>
      </div>

      <h2>User Management</h2>
      <ul className="user-list">
        {users.map((user) => (
          <li key={user.id}>
            {user.name} 
            <button onClick={() => handleApproveUser(user.id)}>Approve</button> 
            <button onClick={() => handleRejectUser(user.id)}>Reject</button> 
            <button onClick={() => handleDeleteUser(user.id)}>Delete</button>
          </li>
        ))}
      </ul>

      <h2>Event Management</h2>
      <ul className="event-list">
        {events.map((event) => (
          <li key={event.id}>
            {event.title} 
            <button onClick={() => handleApproveEvent(event.id)}>Approve</button> 
            <button onClick={() => handleRejectEvent(event.id)}>Reject</button>
          </li>
        ))}
      </ul>

      <h2>Advertisement Management</h2>
      <ul className="ad-list">
        {ads.map((ad) => (
          <li key={ad.id}>
            {ad.title} 
            <button onClick={() => handleApproveAd(ad.id)}>Approve</button> 
            <button onClick={() => handleRejectAd(ad.id)}>Reject</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
