// src/pages/Events.js

import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Assuming you're using a calendar library
import './Events.css'; // Import the CSS for styling

const Events = () => {
  const [value, setValue] = useState(new Date());

  const handleDateChange = (date) => {
    setValue(date);
  };

  // Filter options
  const filterOptions = [
    { value: '', label: 'All Events' },
    { value: 'upcoming', label: 'Upcoming Events' },
    { value: 'past', label: 'Past Events' },
    { value: 'category1', label: 'Category 1' },
    { value: 'category2', label: 'Category 2' },
  ];

  const [selectedFilter, setSelectedFilter] = useState('');

  const handleFilterChange = (event) => {
    setSelectedFilter(event.target.value);
  };

  return (
    <div className="events-container">
      <h1>Events</h1>
      <div className="events-content">
        <div className="calendar-container">
          <Calendar
            onChange={handleDateChange}
            value={value}
            className="custom-calendar"
          />
        </div>
        <div className="filter-container">
          <label htmlFor="filter">Filter Events: </label>
          <select id="filter" value={selectedFilter} onChange={handleFilterChange}>
            {filterOptions.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </div>
      </div>
      <div className="events-list">
        {/* Render the events based on the selected date and filter */}
        <h2>Available Events</h2>
        {/* Example list of events */}
        {/* Replace this section with your dynamic event rendering */}
        <div className="event-item">Event 1: {value.toDateString()}</div>
        <div className="event-item">Event 2: {value.toDateString()}</div>
      </div>
    </div>
  );
};

export default Events;
