import React, { useState } from 'react';
import Calendar from 'react-calendar';
import './Events.css'; // Import your CSS file for styles

const Events = () => {
    const [date, setDate] = useState(new Date());
    
    // Sample event data
    const events = [
        {
            id: 1,
            title: 'Goa Carnival',
            description: 'A vibrant celebration with parades and music.',
            date: '2024-09-15',
            img: '../Images/a-1.jpg'
        },
        {
            id: 2,
            title: 'Shigmo Festival',
            description: 'A cultural festival showcasing Goan traditions.',
            date: '2024-09-20',
            img: '../Images/a-2.jpg'
        },
        {
            id: 3,
            title: 'Feast of St. Joseph',
            description: 'Celebrated by the local community with feasting and fun.',
            date: '2024-09-25',
            img: '../Images/a-3.jpg'
        },
        // Add more events as needed
    ];

    const handleDateChange = (newDate) => {
        setDate(newDate);
    };

    const filteredEvents = events.filter(event =>
        new Date(event.date).toDateString() === date.toDateString()
    );

    return (
        <div className="container">
            <div className="calendar-container">
                <h2>Events Calendar</h2>
                <div className="calendar">
                    <Calendar
                        onChange={handleDateChange}
                        value={date}
                    />
                </div>
            </div>

            <div className="main-container">
                <div className="filters">
                    <div className="filter-card" id="date-filter">
                        <h3>Date</h3>
                        <div className="filter-options">
                            <button>Today</button>
                            <button>Tomorrow</button>
                            <button>This Weekend</button>
                        </div>
                    </div>
                    <div className="filter-card" id="category-filter">
                        <h3>Categories</h3>
                        <div className="filter-options">
                            <button>Category 1</button>
                            <button>Category 2</button>
                            <button>Category 3</button>
                        </div>
                    </div>
                </div>

                <div className="event-cards">
                    {filteredEvents.length > 0 ? (
                        filteredEvents.map(event => (
                            <div key={event.id} className="dispEvent">
                                <img src={event.img} alt={event.title} />
                                <h4>{event.title}</h4>
                                <p>{event.description}</p>
                            </div>
                        ))
                    ) : (
                        <p>No events for this date.</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Events;
