import React, { useState } from 'react';
import './Shop.css'; // Import the Shop styles

const Shop = () => {
  const [products] = useState([
    { id: 1, name: 'Product 1', price: 20, image: 'path/to/image1.jpg' },
    { id: 2, name: 'Product 2', price: 30, image: 'path/to/image2.jpg' },
    { id: 3, name: 'Product 3', price: 25, image: 'path/to/image3.jpg' },
    { id: 4, name: 'Product 4', price: 35, image: 'path/to/image4.jpg' },
    { id: 5, name: 'Product 5', price: 15, image: 'path/to/image5.jpg' },
    { id: 6, name: 'Product 6', price: 40, image: 'path/to/image6.jpg' },
  ]);
  
  const [cart, setCart] = useState([]); // State to hold cart items
  const [showCheckout, setShowCheckout] = useState(false); // State to toggle checkout form
  const [paymentInfo, setPaymentInfo] = useState({ cardNumber: '', expiry: '', cvv: '' }); // State for payment info

  // Handle adding product to cart
  const handleAddToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]); // Add product to cart
  };

  // Handle removing product from cart
  const handleRemoveFromCart = (itemToRemove) => {
    setCart((prevCart) => prevCart.filter(item => item.id !== itemToRemove.id));
  };

  // Handle checkout form submission
  const handleCheckout = (e) => {
    e.preventDefault();

    // Validate payment information
    const isValidCardNumber = /^\d{16}$/.test(paymentInfo.cardNumber);
    const isValidExpiry = /^(0[1-9]|1[0-2])\/?([0-9]{2})$/.test(paymentInfo.expiry);
    const isValidCVV = /^\d{3}$/.test(paymentInfo.cvv);

    if (!isValidCardNumber || !isValidExpiry || !isValidCVV) {
      alert('Please enter valid payment information.');
      return;
    }

    // Process payment (this is a mock; you'd integrate with a payment gateway here)
    alert(`Payment processed for ${cart.length} items!`);
    // Reset cart after successful payment
    setCart([]);
    setShowCheckout(false);
    setPaymentInfo({ cardNumber: '', expiry: '', cvv: '' }); // Clear payment info
  };

  return (
    <div className="shop-container">
      <h1>Shop</h1>

      {/* Display Products */}
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>${product.price}</p>
              <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </div>

      {/* Display Cart */}
      <div className="cart-container">
        <h2>Cart</h2>
        {cart.length > 0 ? (
          cart.map((item, index) => (
            <div key={index} className="cart-item">
              <h3>{item.name}</h3>
              <p>${item.price}</p>
              <button onClick={() => handleRemoveFromCart(item)}>Remove</button>
            </div>
          ))
        ) : (
          <p>No items in the cart.</p>
        )}

        {/* Checkout Button */}
        {cart.length > 0 && (
          <button className="checkout-button" onClick={() => setShowCheckout(!showCheckout)}>
            Checkout
          </button>
        )}
      </div>

      {/* Checkout Form */}
      {showCheckout && cart.length > 0 && (
        <form className="checkout-form" onSubmit={handleCheckout}>
          <h3>Payment Information</h3>
          <input
            type="text"
            placeholder="Card Number (16 digits)"
            value={paymentInfo.cardNumber}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, cardNumber: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Expiry Date (MM/YY)"
            value={paymentInfo.expiry}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, expiry: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="CVV (3 digits)"
            value={paymentInfo.cvv}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, cvv: e.target.value })}
            required
          />
          <button type="submit">Pay Now</button>
        </form>
      )}
    </div>
  );
};

export default Shop;
