import React, { useState } from "react";
import Calendar from "react-calendar"; // Assuming you're using a library like 'react-calendar'
import 'react-calendar/dist/Calendar.css'; // Import styles for the calendar
import './Events.css'; // For custom styles

const EventsPage = () => {
  const [date, setDate] = useState(new Date());
  const [category, setCategory] = useState("");
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);

  const events = [
    { id: 1, title: "Festival of Lights", date: "2024-10-15", category: "Culture" },
    { id: 2, title: "Goa Carnival", date: "2024-11-05", category: "Music" },
    // More events here
  ];

  const handleDateChange = (newDate) => setDate(newDate);

  return (
    <div className="events-container">
      <h1>Upcoming Events</h1>

      <div className="filters">
        <div className="filter-card">
          <h3>Select Date</h3>
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        <div className="filter-card">
          <h3>Select Category</h3>
          <button onClick={() => setShowCategoryDropdown(!showCategoryDropdown)}>
            {category || "Choose a category"}
          </button>
          {showCategoryDropdown && (
            <div className="dropdown-content">
              <p onClick={() => setCategory("Culture")}>Culture</p>
              <p onClick={() => setCategory("Music")}>Music</p>
              <p onClick={() => setCategory("Art")}>Art</p>
            </div>
          )}
        </div>
      </div>

      <div className="events-list">
        <div className="row">
          {events.map((event, index) => (
            <div
              key={event.id}
              className={`event-card ${index < 3 ? "top-row" : "bottom-row"}`}
            >
              <h3>{event.title}</h3>
              <p>Date: {event.date}</p>
              <p>Category: {event.category}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default EventsPage;
