// src/pages/Services.js
import React, { useState } from 'react';

const Services = ({ setBookings }) => {
  // Sample service data - this could be fetched from an API
  const [searchTerm, setSearchTerm] = useState(''); // State for search input
  const services = [
    {
      id: 1,
      name: "Guitar Lessons",
      price: 50,
      contact: "123-456-7890",
    },
    {
      id: 2,
      name: "Photography Session",
      price: 150,
      contact: "987-654-3210",
    },
    {
      id: 3,
      name: "Cooking Classes",
      price: 75,
      contact: "555-666-7777",
    },
    {
      id: 4,
      name: "Yoga Instructor",
      price: 40,
      contact: "888-999-0000",
    },
    // Add more services as needed
  ];

  // Handle booking service function
  const handleBookService = (service) => {
    const booking = {
      serviceId: service.id,
      serviceName: service.name,
      price: service.price,
      contact: service.contact,
    };

    setBookings((prevBookings) => [...prevBookings, booking]);

    alert(`${service.name} has been booked!`);
  };

  // Filter services based on the search term
  const filteredServices = services.filter(service =>
    service.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div>
      <h1>Services</h1>
      <input
        type="text"
        placeholder="Search for services..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
        style={{ marginBottom: '20px', padding: '10px', width: '100%', maxWidth: '400px' }}
      />
      <div style={{ display: 'flex', flexWrap: 'wrap', gap: '20px' }}>
        {filteredServices.length > 0 ? (
          filteredServices.map((service) => (
            <div key={service.id} style={{
              border: '1px solid #ccc',
              borderRadius: '8px',
              padding: '15px',
              width: '200px', 
              boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
            }}>
              <h2>{service.name}</h2>
              <p>Price: ${service.price}</p>
              <p>Contact: {service.contact}</p>
              <button onClick={() => handleBookService(service)}>Book Now</button>
            </div>
          ))
        ) : (
          <p>No services found.</p>
        )}
      </div>
    </div>
  );
};

export default Services;
