import React, { useState } from "react";
import Calendar from "react-calendar";
import 'react-calendar/dist/Calendar.css'; // Ensure Calendar styles are imported
import './Events.css'; // Import your custom styles

const EventsPage = () => {
  const [date, setDate] = useState(new Date());
  const [category, setCategory] = useState("");
  const [showCategoryDropdown, setShowCategoryDropdown] = useState(false);

  const events = [
    { id: 1, title: "Festival of Lights", date: "2024-10-15", category: "Culture" },
    { id: 2, title: "Goa Carnival", date: "2024-11-05", category: "Music" },
    // Add more events as needed
  ];

  const handleDateChange = (newDate) => setDate(newDate);

  return (
    <div className="main-container">
      {/* Filters Section */}
      <div className="filters">
        {/* Date Filter */}
        <div className="filter-card calendar-container">
          <h3>Select Date</h3>
          <Calendar onChange={handleDateChange} value={date} />
        </div>

        {/* Category Filter */}
        <div className="filter-card">
          <h3>Select Category</h3>
          <div className="filter-options">
            <button onClick={() => setCategory("Culture")}>Culture</button>
            <button onClick={() => setCategory("Music")}>Music</button>
            <button onClick={() => setCategory("Art")}>Art</button>
          </div>
        </div>
      </div>

      {/* Events Section */}
      <div className="event-cards">
        {events.map((event) => (
          <div key={event.id} className="dispEvent">
            <img src={`https://via.placeholder.com/150`} alt={event.title} />
            <h3>{event.title}</h3>
            <p>Date: {event.date}</p>
            <p>Category: {event.category}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventsPage;
