import React, { useState } from "react";
import Calendar from "./Calendar"; // Import the renamed Calendar component
import './Events.css'; // Import your custom styles

const CalendarPage = () => {
  const [date, setDate] = useState(new Date());

  // Dummy event data
  const events = [
    { id: 1, title: "Festival of Lights", date: "2024-10-15", category: "Culture", imageUrl: "https://via.placeholder.com/150" },
    { id: 2, title: "Goa Carnival", date: "2024-11-05", category: "Music", imageUrl: "https://via.placeholder.com/150" },
    { id: 3, title: "Food Fiesta", date: "2024-12-01", category: "Food", imageUrl: "https://via.placeholder.com/150" },
    { id: 4, title: "Art Exhibition", date: "2024-11-10", category: "Art", imageUrl: "https://via.placeholder.com/150" },
    { id: 5, title: "Film Festival", date: "2024-11-18", category: "Film", imageUrl: "https://via.placeholder.com/150" },
    { id: 6, title: "Cultural Dance Show", date: "2024-10-25", category: "Dance", imageUrl: "https://via.placeholder.com/150" }
  ];

  const handleDateChange = (newDate) => setDate(newDate);

  return (
    <div className="main-container">
      {/* Full Width Calendar */}
      <Calendar date={date} onDateChange={handleDateChange} />

      {/* Content Section for Filters and Events */}
      <div className="content-container">
        {/* Filters Section */}
        <div className="filters">
          {/* Filter Card */}
          <div className="filter-card">
            <h4>Category Filter</h4>
            <button>Culture</button>
            <button>Music</button>
            <button>Food</button>
            <button>Art</button>
            <button>Film</button>
            <button>Dance</button>
          </div>
        </div>

        {/* Events Section */}
        <div className="event-cards">
          {events.map((event) => (
            <div key={event.id} className="dispEvent">
              <img src={event.imageUrl} alt={event.title} />
              <h3>{event.title}</h3>
              <p>Date: {event.date}</p>
              <p>Category: {event.category}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CalendarPa;
