import React, { useState } from "react";
import "./FormModal.css"; // Add relevant styles

const FormModal = ({ role, closeModal }) => {
  const [file, setFile] = useState(null);
  const [termsChecked, setTermsChecked] = useState(false); // For Seller and Host terms and conditions
  const [formData, setFormData] = useState({
    serviceType: "",
    price: "",
    description: "",
    title: "",
  });

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleTermsChange = () => {
    setTermsChecked(!termsChecked);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle form submission logic based on role
    console.log(`${role} form submitted`, formData, file);
  };

  return (
    <div className="modal-overlay">
      <div className="modal-content">
        <h2>{role} Registration</h2>
        <form onSubmit={handleSubmit}>
          {role === "Seller" && (
            <>
              <label>
                Upload your product details (PDF):
                <input type="file" onChange={handleFileChange} required />
              </label>
              <label>
                <input type="checkbox" checked={termsChecked} onChange={handleTermsChange} />
                I agree to the terms and conditions
              </label>
              <button type="submit" disabled={!termsChecked}>Submit</button>
            </>
          )}

          {role === "Host" && (
            <>
              <label>Event Title:</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
              <label>Event Description:</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              />
              <label>
                <input
                  type="checkbox"
                  checked={termsChecked}
                  onChange={handleTermsChange}
                />
                I agree to all the terms and conditions
              </label>
              <button type="submit" disabled={!termsChecked}>Submit</button>
            </>
          )}

          {role === "Artisan" && (
            <>
              <label>Type of Service:</label>
              <input
                type="text"
                name="serviceType"
                value={formData.serviceType}
                onChange={handleInputChange}
                required
              />
              <label>Price:</label>
              <input
                type="text"
                name="price"
                value={formData.price}
                onChange={handleInputChange}
                required
              />
              <label>Description:</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              />
              <button type="submit">Submit</button>
            </>
          )}

          {role === "Other" && (
            <>
              <label>Inquiry Title:</label>
              <input
                type="text"
                name="title"
                value={formData.title}
                onChange={handleInputChange}
                required
              />
              <label>Description:</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleInputChange}
                required
              />
              <button type="submit">Submit</button>
            </>
          )}

          {role === "Advertise" && (
            <>
              <label>Upload your advertisement image:</label>
              <input type="file" onChange={handleFileChange} required />
              <button type="submit">Submit</button>
            </>
          )}

          <button type="button" onClick={closeModal} className="close-button">
            Close
          </button>
        </form>
      </div>
    </div>
  );
};

export default FormModal;
