// src/pages/Services.js

import React, { useState } from 'react';
import './Services.css'; // Ensure you have appropriate styles defined

const Services = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [services, setServices] = useState([]);

  const handleSearch = (e) => {
    e.preventDefault();
    // Mock services data
    const mockServices = [
      { id: 1, name: 'Guitar Lessons', description: 'Learn to play guitar in a fun way.' },
      { id: 2, name: 'Photography Services', description: 'Capture your special moments.' },
      { id: 3, name: 'Event Planning', description: 'Let us handle your next event.' },
    ];
    // Filter based on searchTerm
    const filteredServices = mockServices.filter(service =>
      service.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setServices(filteredServices);
  };

  return (
    <div className="services-container" style={{ width: '80%', margin: 'auto' }}>
      <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for services..."
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '10px',
            borderRadius: '5px',
            backgroundColor: '#6A0D91',
            color: '#fff',
            border: 'none',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Search
        </button>
      </form>

      <div className="service-list" style={{ display: 'flex', flexWrap: 'wrap' }}>
        {services.length === 0 ? (
          <p>No services found.</p>
        ) : (
          services.map((service) => (
            <div
              className="service-card"
              key={service.id}
              style={{
                margin: '10px',
                border: '1px solid #ccc',
                padding: '10px',
                borderRadius: '5px',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                width: '30%', // Adjust to fit 3 cards in a row
                minWidth: '200px', // Ensure cards are not too small
              }}
            >
              <h3>{service.name}</h3>
              <p>{service.description}</p>
              <button className='search-button'
                onClick={() => {/* Functionality to book service */}}
                style={{
                  padding: '10px',
                  borderRadius: '5px',
                  backgroundColor: '#71BC61',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Book Service
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default Services;
