// src/pages/Events.js
import React from 'react';
import Calendar from '../components/Calendar'; // Ensure the path is correct
import './Events.css'; // Styles for the events page

const Events = () => {
    return (
        <div className="events-page">
            <h1>Events</h1>
            <div className="main-container">
                <Calendar /> {/* Calendar component */}
                <div className="filters">
                    {/* Filters will go here */}
                </div>
                <div className="event-cards">
                    {/* Display events in cards */}
                    <div className="dispEvent">
                        <img src="event1.jpg" alt="Event 1" />
                        <h3>Event Title 1</h3>
                        <p>Event Description 1</p>
                        <button>Enroll</button>
                    </div>
                    <div className="dispEvent">
                        <img src="event2.jpg" alt="Event 2" />
                        <h3>Event Title 2</h3>
                        <p>Event Description 2</p>
                        <button>Enroll</button>
                    </div>
                    <div className="dispEvent">
                        <img src="event3.jpg" alt="Event 3" />
                        <h3>Event Title 3</h3>
                        <p>Event Description 3</p>
                        <button>Enroll</button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Events;
