import React, { useState } from 'react';
import Calendar from 'react-calendar'; // Importing the Calendar component
import 'react-calendar/dist/Calendar.css'; // Importing Calendar CSS
import './Events.css'; // Importing custom CSS

const Events = () => {
  // State to manage selected date and event type filter
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedType, setSelectedType] = useState('');

  // Sample events data
  const events = [
    { id: 1, title: 'Goa Cultural Fest', date: '2024-10-05', type: 'Festival' },
    { id: 2, title: 'Art Exhibition', date: '2024-10-06', type: 'Exhibition' },
    { id: 3, title: 'Music Concert', date: '2024-10-07', type: 'Concert' },
    { id: 4, title: 'Food Festival', date: '2024-10-08', type: 'Festival' }
  ];

  // Handle date selection from the calendar
  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  // Filter the events based on selected date and type
  const filteredEvents = events.filter(event => {
    const isSameDate = event.date === selectedDate.toISOString().split('T')[0];
    const isSameType = selectedType ? event.type === selectedType : true;
    return isSameDate && isSameType;
  });

  return (
    <div className="events-container">
      <h1 className="page-title">Events Calendar</h1>

      {/* Calendar Section */}
      <div className="calendar-section">
        <Calendar
          onChange={handleDateChange}
          value={selectedDate}
          className="large-calendar"
        />
      </div>

      {/* Filters Section */}
      <div className="filters-section">
        <h3>Filter Events</h3>
        <label>Event Type:</label>
        <select onChange={(e) => setSelectedType(e.target.value)} value={selectedType}>
          <option value="">All</option>
          <option value="Festival">Festival</option>
          <option value="Exhibition">Exhibition</option>
          <option value="Concert">Concert</option>
        </select>
      </div>

      {/* Display Filtered Events */}
      <h2>Events on {selectedDate.toDateString()}</h2>
      <div className="events-list">
        {filteredEvents.length > 0 ? (
          filteredEvents.map(event => (
            <div key={event.id} className="event-card">
              <h3>{event.title}</h3>
              <p>Type: {event.type}</p>
              <p>Date: {event.date}</p>
            </div>
          ))
        ) : (
          <p>No events on this date.</p>
        )}
      </div>
    </div>
  );
};

export default Events;
