// src/pages/Shop.js

import React, { useState } from 'react';
import './Shop.css'; // Import the Shop styles

const Shop = () => {
  const [products, setProducts] = useState([]); // State to hold products
  const [cart, setCart] = useState([]); // State to hold cart items
  const [newProduct, setNewProduct] = useState({ name: '', price: '', image: null }); // State for new product
  const [showAddProduct, setShowAddProduct] = useState(false); // State to toggle add product form
  const [showCheckout, setShowCheckout] = useState(false); // State to toggle checkout form
  const [paymentInfo, setPaymentInfo] = useState({ cardNumber: '', expiry: '', cvv: '' }); // State for payment info

  // Handle product submission
  const handleAddProduct = (e) => {
    e.preventDefault();

    const productWithImage = {
      id: products.length + 1,
      name: newProduct.name,
      price: newProduct.price,
      image: URL.createObjectURL(newProduct.image), // Create a URL for the uploaded image
    };

    // Add new product to the list
    setProducts([...products, productWithImage]);
    // Reset form fields
    setNewProduct({ name: '', price: '', image: null });
    setShowAddProduct(false); // Hide the form after submission
  };

  // Handle adding product to cart
  const handleAddToCart = (product) => {
    setCart([...cart, product]); // Add product to cart
  };

  // Handle checkout form submission
  const handleCheckout = (e) => {
    e.preventDefault();
    // Process payment (this is a mock; you'd integrate with a payment gateway here)
    alert(`Payment processed for ${cart.length} items!`);
    // Reset cart after successful payment
    setCart([]);
    setShowCheckout(false);
  };

  return (
    <div className="shop-container">
      <h1>Shop</h1>

      {/* Add Product Button */}
      <button className="add-product-button" onClick={() => setShowAddProduct(!showAddProduct)}>
        {showAddProduct ? "Cancel" : "Add Product"}
      </button>

      {/* Add Product Form */}
      {showAddProduct && (
        <form className="add-product-form" onSubmit={handleAddProduct}>
          <input
            type="text"
            placeholder="Product Name"
            value={newProduct.name}
            onChange={(e) => setNewProduct({ ...newProduct, name: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="Price"
            value={newProduct.price}
            onChange={(e) => setNewProduct({ ...newProduct, price: e.target.value })}
            required
          />
          <input
            type="file"
            accept="image/*"
            onChange={(e) => setNewProduct({ ...newProduct, image: e.target.files[0] })}
            required
          />
          <button type="submit">Submit</button>
        </form>
      )}

      {/* Display Products */}
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <img src={product.image} alt={product.name} />
              <h3>{product.name}</h3>
              <p>${product.price}</p>
              <button onClick={() => handleAddToCart(product)}>Add to Cart</button>
            </div>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </div>

      {/* Display Cart */}
      <div className="cart-container">
        <h2>Cart</h2>
        {cart.length > 0 ? (
          cart.map((item, index) => (
            <div key={index} className="cart-item">
              <h3>{item.name}</h3>
              <p>${item.price}</p>
            </div>
          ))
        ) : (
          <p>No items in the cart.</p>
        )}

        {/* Checkout Button */}
        {cart.length > 0 && (
          <button className="checkout-button" onClick={() => setShowCheckout(!showCheckout)}>
            Checkout
          </button>
        )}
      </div>

      {/* Checkout Form */}
      {showCheckout && (
        <form className="checkout-form" onSubmit={handleCheckout}>
          <h3>Payment Information</h3>
          <input
            type="text"
            placeholder="Card Number"
            value={paymentInfo.cardNumber}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, cardNumber: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Expiry Date (MM/YY)"
            value={paymentInfo.expiry}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, expiry: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="CVV"
            value={paymentInfo.cvv}
            onChange={(e) => setPaymentInfo({ ...paymentInfo, cvv: e.target.value })}
            required
          />
          <button type="submit">Pay Now</button>
        </form>
      )}
    </div>
  );
};

export default Shop;
