import React, { useState } from 'react';
import Calendar from 'react-calendar';
import './Events.css';

const Events = () => {
    // Dummy data for events
    const eventsData = [
        { id: 1, title: 'Event 1', description: 'Description for Event 1', date: new Date(2024, 9, 15) },
        { id: 2, title: 'Event 2', description: 'Description for Event 2', date: new Date(2024, 9, 16) },
        { id: 3, title: 'Event 3', description: 'Description for Event 3', date: new Date(2024, 9, 20) },
        { id: 4, title: 'Event 4', description: 'Description for Event 4', date: new Date(2024, 9, 22) },
        { id: 5, title: 'Event 5', description: 'Description for Event 5', date: new Date(2024, 9, 23) },
    ];

    const [events, setEvents] = useState(eventsData);
    const [selectedDate, setSelectedDate] = useState(new Date());

    // Function to handle date selection
    const onDateChange = (date) => {
        setSelectedDate(date);
    };

    // Function to filter events
    const handleFilter = (filterType) => {
        const today = new Date();
        let filteredEvents;

        switch (filterType) {
            case 'today':
                filteredEvents = eventsData.filter(event => event.date.toDateString() === today.toDateString());
                break;
            case 'tomorrow':
                const tomorrow = new Date();
                tomorrow.setDate(today.getDate() + 1);
                filteredEvents = eventsData.filter(event => event.date.toDateString() === tomorrow.toDateString());
                break;
            case 'weekend':
                filteredEvents = eventsData.filter(event => {
                    const day = event.date.getDay();
                    return day === 0 || day === 6; // Sunday or Saturday
                });
                break;
            default:
                filteredEvents = eventsData; // Reset to all events
        }

        setEvents(filteredEvents);
    };

    return (
        <div className="container">
            <div className="calendar-container">
                <h2>Events Calendar</h2>
                <Calendar
                    onChange={onDateChange}
                    value={selectedDate}
                    tileClassName={({ date }) =>
                        events.find(event => event.date.toDateString() === date.toDateString()) ? 'react-calendar__tile--event' : null
                    }
                />
            </div>

            <div className="main-container">
                <div className="filters">
                    <div className="filter-card" id="date-filter">
                        <h3>Date</h3>
                        <div className="filter-options">
                            <button onClick={() => handleFilter('today')}>Today</button>
                            <button onClick={() => handleFilter('tomorrow')}>Tomorrow</button>
                            <button onClick={() => handleFilter('weekend')}>This Weekend</button>
                        </div>
                    </div>
                    <div className="filter-card" id="category-filter">
                        <h3>Categories</h3>
                        <div className="filter-options">
                            <button>Category 1</button>
                            <button>Category 2</button>
                            <button>Category 3</button>
                        </div>
                    </div>
                </div>

                <div className="event-cards">
                    {events.map(event => (
                        <div className="dispEvent" key={event.id}>
                            <img src={`../Images/a-${event.id}.jpg`} alt={event.title} />
                            <h4>{event.title}</h4>
                            <p>{event.description}</p>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Events;
