// src/pages/Services.js

import React, { useState } from 'react';
import './Services.css'; // Ensure you have appropriate styles defined

const Services = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [bookings, setBookings] = useState([]);

  // Initial services data (including contact numbers)
  const services = [
    { id: 1, name: 'Guitar Lessons', description: 'Learn to play guitar in a fun way.', contact: '123-456-7890' },
    { id: 2, name: 'Photography Services', description: 'Capture your special moments.', contact: '987-654-3210' },
    { id: 3, name: 'Event Planning', description: 'Let us handle your next event.', contact: '456-789-1234' },
  ];

  const handleSearch = (e) => {
    e.preventDefault();
  };

  // Filter services based on searchTerm
  const filteredServices = services.filter(service =>
    service.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const handleBookService = (service) => {
    setBookings((prevBookings) => [...prevBookings, service]);
    alert(`Booked ${service.name}. Contact: ${service.contact}`); // Notify the user or handle booking logic
  };

  return (
    <div className="services-container" style={{ width: '80%', margin: 'auto' }}>
      <form onSubmit={handleSearch} style={{ marginBottom: '20px' }}>
        <input
          type="text"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          placeholder="Search for services..."
          style={{
            width: '100%',
            padding: '10px',
            borderRadius: '5px',
            border: '1px solid #ccc',
          }}
        />
        <button
          type="submit"
          style={{
            padding: '10px',
            borderRadius: '5px',
            backgroundColor: '#5fa74d',
            color: '#fff',
            border: 'none',
            cursor: 'pointer',
            marginTop: '10px',
          }}
        >
          Search
        </button>
      </form>

      <div className="service-list" style={{ display: 'flex', flexWrap: 'wrap' }}>
        {filteredServices.length === 0 ? (
          <p>No services found.</p>
        ) : (
          filteredServices.map((service) => (
            <div
              className="service-card"
              key={service.id}
              style={{
                margin: '10px',
                border: '1px solid #ccc',
                padding: '10px',
                borderRadius: '5px',
                boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
                width: '30%',
                minWidth: '200px',
              }}
            >
              <h3>{service.name}</h3>
              <p>{service.description}</p>
              <button className='search-button'
                onClick={() => handleBookService(service)}
                style={{
                  padding: '10px',
                  borderRadius: '5px',
                  backgroundColor: '#71BC61',
                  color: '#fff',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Book Service
              </button>
            </div>
          ))
        )}
      </div>

      {/* Display booked services */}
      <h2>Booked Services</h2>
      <ul className="booked-services-list">
        {bookings.length === 0 ? (
          <li>No services booked yet.</li>
        ) : (
          bookings.map((booking, index) => (
            <li key={index}>
              {booking.name} - Contact: {booking.contact}
            </li>
          ))
        )}
      </ul>
    </div>
  );
};

export default Services;
